import { useState } from "react";
import styles from "./CreateRecipe.module.css";
import { validate } from "./validation";

export default function CreateRecipe() {
  const [input, setInput] = useState({
    name: "",
    image: "",
    summary: "",
    health_score: "",
    instructions: "",
  });
  const [errors, setErrors] = useState({
    name: "",
    image: "",
    summary: "",
    health_score: "",
    instructions: "",
  });

  const handleChange = (event) => {
    const property = event.target.name;
    const value = event.target.value;
    setInput({ ...input, [property]: value });
    validate({ ...input, [property]: value }, errors, setErrors, property);
  };
  return (
    <div className={styles.Contenedor}>
      <form className={styles.ContenedorForm}>
        <div className={styles.Form}>
          <p>{errors.name}</p>
          <p>{errors.image}</p>
          <p>{errors.summary}</p>
          <p>{errors.health_score}</p>
          <p>{errors.instructions}</p>
          <label>
            Nombre de la receta
            <input
              type="text"
              name="name"
              onChange={handleChange}
              value={input.name}
            />
          </label>
          <label>
            Link a la imagen
            <input
              type="text"
              name="image"
              onChange={handleChange}
              value={input.image}
            />
          </label>
          <label>
            summary
            <textarea
              name="summary"
              onChange={handleChange}
              value={input.summary}
            />
          </label>
          <label>
            Puntaje saludable
            <input
              type="range"
              min="0"
              max="100"
              name="health_score"
              onChange={handleChange}
              value={input.health_score}
            />
          </label>
          <label>
            Preparacion
            <textarea
              name="instructions"
              onChange={handleChange}
              value={input.instructions}
            />
          </label>
          <button>Submit</button>
        </div>
      </form>
      <div className={styles.Resultado}>
        aca voy a mostrar como un "resultado" en caso de creacion correcta o no
      </div>
    </div>
  );
}
