import { useEffect, useState } from "react";
import styles from "./CreateRecipe.module.css";
import { validate } from "./validation";
import { useDispatch, useSelector } from "react-redux";
import { crearReceta, pedirDietas } from "../../Redux/action";
import axios from "axios";

export default function CreateRecipe() {
  const dietas = useSelector((state) => state.allDiets);
  const dispatch = useDispatch();

  if (!dietas.length) {
    dispatch(pedirDietas());
  }

  const [input, setInput] = useState({
    name: "",
    image: "",
    summary: "",
    health_score: "",
    instructions: "",
    diets: [],
  });
  const [errors, setErrors] = useState({
    name: "",
    image: "",
    summary: "",
    health_score: "",
    instructions: "",
  });

  const [created, SetCreated] = useState("");

  const crearRecipe = (arg) => {
    const endpoint = "http://localhost:3001/recipe";
    console.log(arg);
    axios
      .post(endpoint, arg)
      .then(() => {
        alert("Receta creada correctamente");
      })
      .catch((error) => {
        console.log(error);
        alert("hubo un error");
      });
  };
  const handleChange = (event) => {
    event.preventDefault();
    const property = event.target.name;
    const value = event.target.value;
    setInput({ ...input, [property]: value });
    validate({ ...input, [property]: value }, errors, setErrors, property);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    crearRecipe(input);
  };
  const handleDiet = (event) => {
    if (!input.diets.includes(event.target.value)) {
      let aux = [...input.diets];
      aux.push(event.target.value);
      setInput({ ...input, ["diets"]: aux });
    } else {
      let aux = [...input.diets];
      aux.filter((element) => element !== event.target.value);
      setInput({ ...input, ["diets"]: aux });
    }
  };
  return (
    <div className={styles.Contenedor}>
      <div className={styles.ContenedorForm}>
        <form className={styles.Form} onSubmit={handleSubmit}>
          <label className={styles.Name}>
            Nombre de la receta
            <input
              type="text"
              name="name"
              onChange={handleChange}
              value={input.name}
            />
          </label>
          <label className={styles.Image}>
            Link a la imagen
            <input
              type="text"
              name="image"
              onChange={handleChange}
              value={input.image}
            />
          </label>
          <label className={styles.Summary}>
            summary
            <textarea
              name="summary"
              onChange={handleChange}
              value={input.summary}
            />
          </label>
          <label className={styles.HealthScore}>
            Puntaje saludable: {input.health_score}
            <input
              type="range"
              min="0"
              max="100"
              name="health_score"
              onChange={handleChange}
              value={input.health_score}
            />
          </label>
          <label className={styles.Preparacion}>
            Preparacion
            <textarea
              name="instructions"
              onChange={handleChange}
              value={input.instructions}
            />
          </label>
          <div className={styles.Dietas}>
            {dietas.length ? (
              dietas.map((element) => {
                return (
                  // <label>
                  //   {element.name}
                  <div className={styles.ContenedorDietas}>
                    <button
                      type="checkbox"
                      value={element.id}
                      name={element.name}
                      onChange={handleDiet}
                    >
                      {element.name}
                    </button>
                  </div>
                  // </label>
                );
              })
            ) : (
              <div>waiting...</div>
            )}
          </div>
          <button>Submit</button>
        </form>
      </div>
      <div className={styles.Resultado}>
        <p>{errors.name}</p>
        <p>{errors.image}</p>
        <p>{errors.summary}</p>
        <p>{errors.health_score}</p>
        <p>{errors.instructions}</p>
        {created === "" ? (
          <p>esperando</p>
        ) : created === true ? (
          <p>creado</p>
        ) : (
          <p>no se pudo crear</p>
        )}
      </div>
    </div>
  );
}
